// A map to hold our base64 encoded audio files
const audioFiles: { [key: string]: string } = {
  peck: 'data:audio/mp3;base64,',
  play_card: 'data:audio/mp3;base64,-',
  gain: 'data:audio/mp3;base64,-',
  event: 'data:audio/mp3;base64,-',
  upgrade: 'data:audio/mp3;base64,-',
  prestige: 'data:audio/mp3;base64,-',
  music_theme: 'data:audio/mp3;base64,-',
};

// A map to hold the preloaded Audio elements
const audioCache: { [key: string]: HTMLAudioElement } = {};
let musicPlayer: HTMLAudioElement | null = null;

// Function to preload all sounds
export const preloadSounds = () => {
  if (Object.keys(audioCache).length > 0) return; // aalrady loaded
  for (const key in audioFiles) {
    if (Object.prototype.hasOwnProperty.call(audioFiles, key)) {
      const audio = new Audio(audioFiles[key]);
      audio.preload = 'auto';
      audioCache[key] = audio;
    }
  }
};

/**
 * Plays a sound effect from the preloaded cache.
 * It clones the audio node to allow for multiple instances of the same sound to overlap.
 * @param soundName - The key of the sound to play.
 * @param volume - The volume to play the sound at (0.0 to 1.0).
 */
export const playSound = (soundName: keyof typeof audioFiles, volume = 0.5) => {
  if (audioCache[soundName]) {
    const sound = audioCache[soundName].cloneNode() as HTMLAudioElement;
    sound.volume = volume;
    sound.play().catch(error => {
      // Autoplay is often restricted by browsers until the user interacts with the page.
      // This warning is helpful for debugging but common on first load.
      console.warn(`Audio play failed for "${soundName}": ${error}`);
    });
  } else {
    console.warn(`Sound not found: ${soundName}`);
  }
};

/**
 * Plays a background music track.
 * @param trackName - The key of the music track to play.
 * @param volume - The volume to play at.
 * @param loop - Whether the music should loop.
 */
export const playMusic = (trackName: keyof typeof audioFiles, volume = 0.3, loop = true) => {
    if (musicPlayer && !musicPlayer.paused) {
        musicPlayer.pause();
    }
    if (audioCache[trackName]) {
        musicPlayer = audioCache[trackName].cloneNode() as HTMLAudioElement;
        musicPlayer.volume = volume;
        musicPlayer.loop = loop;
        musicPlayer.play().catch(error => console.warn(`Music play failed for "${trackName}": ${error}`));
    } else {
        console.warn(`Music track not found: ${trackName}`);
    }
};

/**
 * Stops the currently playing background music.
 */
export const stopMusic = () => {
    if (musicPlayer) {
        musicPlayer.pause();
        musicPlayer.currentTime = 0;
        musicPlayer = null;
    }
};